// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

library LibAccessManaged {
    struct AccessManagedStorage {
        address _authority;
        bool _consumingSchedule;
    }

    // keccak256(abi.encode(uint256(keccak256("openzeppelin.storage.AccessManaged")) - 1)) & ~bytes32(uint256(0xff))
    bytes32 private constant AccessManagedStorageLocation =
        0xf3177357ab46d8af007ab3fdb9af81da189e1068fefdc0073dca88a2cab40a00;

    function Storage() internal pure returns (AccessManagedStorage storage fs) {
        bytes32 position = AccessManagedStorageLocation;
        assembly {
            fs.slot := position
        }
    }
}

src/facets/funcs.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

import {IVault} from "../interfaces/IVault.sol";
import {MarketHandler} from "../lib/market/MarketHandler.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

uint8 constant usdDecimals = 18; //数量精度

function vault(uint16 market) view returns (IVault) {
    return IVault(MarketHandler.vault(market));
}

function formatCollateral(uint256 amount, address colleteralToken) view returns (uint256) {
    uint8 collateralTokenDigits = IERC20Metadata(colleteralToken).decimals();
    return (amount * (10 ** uint256(collateralTokenDigits))) / (10 ** usdDecimals);
}

function transferOut(address tokenAddress, address _to, uint256 _tokenAmount) {
    // If the token amount is 0, return.
    if (_tokenAmount == 0) return;
    // Format the collateral amount based on the token's decimals.
    _tokenAmount = formatCollateral(_tokenAmount, tokenAddress);
    // Transfer the tokens to the specified address.
    SafeERC20.safeTransfer(IERC20Metadata(tokenAddress), _to, _tokenAmount);
}

src/facets/PositionFacetBase.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;
pragma abicoder v2;

// interfaces
import {IFeeFacet} from "../interfaces/IFeeFacet.sol";
import {IPrice} from "../interfaces/IPrice.sol";
import {IMarketInternal} from "../interfaces/IMarketInternal.sol";
import {IPositionFacet} from "../interfaces/IPositionFacet.sol";
import {IVault} from "../interfaces/IVault.sol";
import {IOrderFacet} from "../interfaces/IOrderFacet.sol";
//================================================
// handlers
import {PositionStorage} from "../lib/position/PositionStorage.sol";
import {MarketHandler} from "../lib/market/MarketHandler.sol";

abstract contract PositionFacetBase {
    function _feeFacet() internal view returns (IFeeFacet) {
        return IFeeFacet(address(this));
    }

    function _marketFacet() internal view returns (IMarketInternal) {
        return IMarketInternal(address(this));
    }

    function _positionFacet() internal view returns (IPositionFacet) {
        return IPositionFacet(address(this));
    }

    function _orderFacet() internal view returns (IOrderFacet) {
        return IOrderFacet(address(this));
    }

    function _updateCumulativeFundingRate(uint16 market) internal {
        (uint256 _longSize, uint256 _shortSize) = PositionStorage.getMarketSizesForBothDirections(market);
        _feeFacet().updateCumulativeFundingRate(market, _longSize, _shortSize); //1
    }

    function _getClosePrice(uint16 market, bool _isLong) internal view returns (uint256 p) {
        return _priceFacet().getPrice(market, !_isLong);
    }

    function _getOpenPrice(uint16 market, bool _isLong) internal view returns (uint256 p) {
        return _priceFacet().getPrice(market, _isLong);
    }

    function _priceFacet() private view returns (IPrice) {
        return IPrice(address(this));
    }
}

