// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "../interfaces/IPositionFacet.sol";
import "../lib/utils/EnumerableValues.sol";
import {PositionProps} from "../lib/types/Types.sol";
import {IPrice} from "../interfaces/IPrice.sol";
import {IAccessManaged} from "../ac/IAccessManaged.sol";
//==========================================================================================
// hanlders
import {PositionHandler} from "../lib/position/PositionHandler.sol";
import {PositionStorage, PositionCache} from "../lib/position/PositionStorage.sol";

contract PositionFacet is IPositionFacet, IAccessManaged {
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableValues for EnumerableSet.AddressSet;
    using EnumerableValues for EnumerableSet.UintSet;

    //==========================================================================================
    //       self functions
    //==========================================================================================
    function _increasePosition(IncreasePositionInputs calldata _data)
        external
        override
        onlySelf
        returns (PositionProps memory result)
    {
        PositionCache memory cache;
        cache.market = _data.market;
        cache.account = _data.account;
        cache.collateralDelta = _data.collateralDelta;
        cache.sizeDelta = _data.sizeDelta;
        cache.markPrice = _data.markPrice;
        cache.fundingRate = _data.fundingRate;
        cache.isLong = _data.isLong;
        return PositionHandler.increasePosition(cache);
    }

    function _decreasePosition(DecreasePositionInputs calldata inputs)
        external
        onlySelf
        returns (PositionProps memory result)
    {
        PositionCache memory cache;
        cache.market = inputs.market;
        cache.account = inputs.account;
        cache.collateralDelta = inputs.collateralDelta;
        cache.sizeDelta = inputs.sizeDelta;
        cache.fundingRate = inputs.fundingRate;
        cache.isLong = inputs.isLong;
        return PositionHandler.decreasePosition(cache);
    }

    function _liquidatePosition(uint16 market, address account, uint256 oraclePrice, bool isLong)
        external
        override
        onlySelf
        returns (PositionProps memory result)
    {
        PositionCache memory cache;
        cache.market = market;
        cache.account = account;
        cache.markPrice = oraclePrice;
        cache.isLong = isLong;
        return PositionHandler.liquidatePosition(cache);
    }

    //==========================================================================================
    //       view functions
    //==========================================================================================

    function getAccountSize(uint16 market, address account) external view returns (uint256, uint256) {
        return PositionStorage.getAccountSizesForBothDirections(market, account);
    }

    function getPosition(uint16 market, address account, uint256 markPrice, bool isLong)
        public
        view
        override
        returns (PositionProps memory)
    {
        return PositionStorage.getPosition(market, account, markPrice, isLong);
    }

    function getMarketSizes(uint16 market) external view returns (uint256, uint256) {
        return PositionStorage.getMarketSizesForBothDirections(market);
    }

    function getPositions(uint16 market, address account)
        external
        view
        returns (PositionProps memory posLong, PositionProps memory posShort)
    {
        return PositionStorage.getPositionsForBothDirections(market, account);
    }

    function getGlobalPosition(uint16 market, bool isLong) external view returns (PositionProps memory) {
        return PositionStorage.getGlobalPosition(market, isLong);
    }

    function containsPositionOfUser(uint16 market, address account) external view returns (bool) {
        PositionStorage.StorageStruct storage ps = PositionStorage.Storage();
        return ps.positions[PositionStorage.storageKey(market, true)][account].size > 0
            || ps.positions[PositionStorage.storageKey(market, false)][account].size > 0;
    }

    function getPositionKeys(uint16 market, uint256 start, uint256 end, bool isLong)
        external
        view
        returns (address[] memory)
    {
        // DONE
        PositionStorage.StorageStruct storage ps = PositionStorage.Storage();
        bytes32 k = PositionStorage.storageKey(market, isLong);
        uint256 len = ps.positionKeys[k].length();
        if (len == 0) {
            return new address[](0);
        }
        if (end > len) end = len;
        return ps.positionKeys[k].valuesAt(start, end);
    }

    function getPositionCount(uint16 market, bool isLong) external view returns (uint256) {
        PositionStorage.StorageStruct storage ps = PositionStorage.Storage();
        return ps.positionKeys[PositionStorage.storageKey(market, isLong)].length();
    }

    function getPNLOfUser(uint16 market, address account, uint256 sizeDelta, uint256 markPrice, bool isLong)
        external
        view
        override
        returns (int256)
    {
        return PositionStorage.getPNL(market, account, sizeDelta, markPrice, isLong);
    }

    function getPNLOfMarket(uint16 market) external view returns (int256 pnl) {
        // TODO
        // uint256 longPrice = IPrice(this).getPrice(market, false);
        // uint256 shortPrice = IPrice(this).getPrice(market, true);
        // pnl = TransferHelper.parseVaultAssetSigned(
        //     PositionHandler.getMarketPNL(market, longPrice, shortPrice), collateralTokenDigits
        // );
    }

    //==========================================================================================
    //       private functions
    //==========================================================================================
}

src/facets/PositionFacetBase.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;
pragma abicoder v2;

// interfaces
import {IFeeFacet} from "../interfaces/IFeeFacet.sol";
import {IPrice} from "../interfaces/IPrice.sol";
import {IPositionFacet, IncreasePositionInputs, DecreasePositionInputs} from "../interfaces/IPositionFacet.sol";
import {IVault} from "../interfaces/IVault.sol";
import {IOrderFacet} from "../interfaces/IOrderFacet.sol";
//================================================
// handlers
import {PositionStorage} from "../lib/position/PositionStorage.sol";
import {MarketHandler} from "../lib/market/MarketHandler.sol";

abstract contract PositionFacetBase {
    function _feeFacet() internal view returns (IFeeFacet) {
        return IFeeFacet(address(this));
    }

    function _positionFacet() internal view returns (IPositionFacet) {
        return IPositionFacet(address(this));
    }

    function _orderFacet() internal view returns (IOrderFacet) {
        return IOrderFacet(address(this));
    }

    function _updateCumulativeFundingRate(uint16 market) internal {
        (uint256 _longSize, uint256 _shortSize) = PositionStorage.getMarketSizesForBothDirections(market);
        _feeFacet().updateCumulativeFundingRate(market, _longSize, _shortSize); //1
    }

    function _getClosePrice(uint16 market, bool _isLong) internal view returns (uint256 p) {
        return _priceFacet().getPrice(market, !_isLong);
    }

    function _getOpenPrice(uint16 market, bool _isLong) internal view returns (uint256 p) {
        return _priceFacet().getPrice(market, _isLong);
    }

    function _priceFacet() private view returns (IPrice) {
        return IPrice(address(this));
    }

    /**
     * @dev Calculates the delta collateral for decreasing a position.
     * @return deltaCollateral The calculated delta collateral.
     */
    function getDecreaseDeltaCollateral(bool isKeepLev, uint256 size, uint256 dSize, uint256 collateral)
        internal
        pure
        returns (uint256 deltaCollateral)
    {
        if (isKeepLev) {
            deltaCollateral = (collateral * dSize) / size;
        } else {
            deltaCollateral = 0;
        }
    }
}

